function addBackgroundEffect(){var e=event.target.closest(".project").querySelector(".project-name");e.classList.add("link-underline-hide")}function removeBackgroundEffect(){var e=event.target.closest(".project").querySelector(".project-name");e.classList.remove("link-underline-hide")}var projects=document.getElementsByClassName("project");for(var i=0;i<projects.length;i++){projects[i].addEventListener("mouseover",addBackgroundEffect,false);projects[i].addEventListener("mouseout",removeBackgroundEffect,false);projects[i].addEventListener("focusin",addBackgroundEffect,false);projects[i].addEventListener("focusout",removeBackgroundEffect,false)}